mapper.insert:

INSERT INTO movie_table (bno, title, genre_code, releaseDate, raiting, director)
SELECT IFNULL(MAX(bno), 0) + 1, #{title}, #{genre}, #{releaseDate}, #{raiting}, #{director}
FROM movie_table;

mapper.select:

SELECT m.bno bno, m.title title, g.genre genre, m.releaseDate releaseDate, m.raiting raiting, m.director director
FROM movie_table m, genre_table g
WHERE m.genre_code = g.genre_code AND m.bno = #{bno};

mapper.update:

UPDATE movie_table
SET title = #{title}, genre_code = #{genre}, releaseDate = #{releaseDate}, director = #{director}
WHERE bno = #{bno};
